import { useState, useEffect } from "react";
import axios from "axios";
import { Link } from "react-router-dom"
import { UpdateTodo } from "./updateTodo"

function TodoCard({ data , handleEdit ,handleDelete }) {
    const { _id, title, description } = data;


    const [editedData, setEditedData] = useState(data);

    
    return (
        <li key={_id}>
            <div className="title-description">
                <h3>{title}</h3>
                <p>{description}</p>
            </div>

            <div className="button-container">
                <button className="button" name={_id} onClick={handleEdit} >edit</button>
                <button name={_id} className="button" onClick={handleDelete}>delete</button>
            </div>
        </li>
    );
}

export function ShowTodoList() {
    const [todo, setTodo] = useState([]);
    const [open, setOpen] = useState(false);
    const [id, setId] = useState(""); 
    const [update, setUpdate] = useState(false);

    useEffect(() => {
        axios
            .get("http://localhost:8000/api/todo")
            .then((res) => {
                console.log(res.data);
                setTodo(res.data);
            })
            .catch((err) => {
                console.log(err);
            });
    }, [update]);

    function handleEdit(e) { // added
        setId(e.target.name); 
        setOpen(true);
    }

    function handleUpdate(updatedTodo) { // added
        console.log("update:", update, !update);
        setUpdate(!update);


        if (updatedTodo) {
            setTodo((prevTodo) =>
                prevTodo.map((todo) =>
                    todo._id === updatedTodo._id ? updatedTodo : todo
                )
            );
        }

    }

    function handleDelete(e) {
        axios.delete(`http://localhost:8000/api/todo/${e.target.name}`);

        setTodo((data) => {
            return data.filter((todo) => todo._id !== e.target.name);
        });
    }

    function handleClose() { // added
        setId("");
        setOpen(false);
    }


    return (
        <section className="container">
            <Link to="/create-todo" className="button-new">
                <button className="button">New</button>
            </Link>
            <section className="contents">
                <h1>TODO</h1>
                <ul className="list-container">
                    {todo.map((data) => (
                        <TodoCard key={data._id} data={data} handleDelete={handleDelete}  handleEdit={handleEdit} />
                    ))}
                </ul>
            </section>


            {open ? (
                <section className="update-container">
                    <div className="update-contents">
                        <p onClick={handleClose} className="close">
                            &times;
                        </p>

                        <UpdateTodo
                            _id={id}
                            handleClose={handleClose}
                            handleEdited={handleUpdate}
                        />
                    </div>
                </section>
            ) : (
                ""
            )}
        </section>
    );


}
